$(document).ready(()=>{$(".slider").slick({infinite:!1,slidesToShow:2,slidesToScroll:1,arrows:!1,dots:!0,responsive:[{breakpoint:1440,settings:{slidesToShow:1,slidesToScroll:1}}]});const e=document.getElementById("js-burger"),s=document.getElementById("js-menu");e.addEventListener("click",()=>{e.classList.toggle("header__burger--opened"),s.classList.toggle("mobile-menu--opened")});const t=document.getElementsByClassName("mobile-menu__toggle");for(item of t)item.addEventListener("click",e=>{item.parentElement.classList.toggle("mobile-menu__parent--opened")},!1)});